    /**-------------------------------------
     * Thực hiện delay call API khi tìm kiếm.
     * Author: quyetkaito (15/03/2022)
    ---------------------------------*/
    lodashDelay: _.debounce(function () {
      this.filterDataServer();
    }, 1200),

    /**--------------------------------
     * Hàm gọi API tìm kiếm dữ liệu từ server.
     * Author: quyetkaito (03/03/2022)
     ----------------------------------*/
    filterDataServer(pageNumber) {
      let me = this; //reset mảng employee

      me.employees = []; //show loading

      me.isShowSkeleton = true; //xử lý đầu vào API

      if (!pageNumber) pageNumber = 1;
      if (!me.pageSize) me.pageSize = 10;
      let value = this.dataFilter.toString(); //gọi api lấy dữ liệu theo filter

      axios.get(`${LOCAL_EMPLOYEE_API_URL}/filter?pageSize=${me.pageSize}&pageNumber=${pageNumber}&textFilter=${value}`).then(response => {
        //nếu có kết quả
        if (response.status == this.$enum.StatusCode.OK) {
          //gán response vào danh sách nhân viên
          me.employees = response.data.Data; //gán tổng số bản ghi

          me.totalEmp = response.data.TotalRecord; //gán tổng số trang

          me.totalPage = response.data.TotalPage; //ẩn loading

          me.isShowSkeleton = false;
        } //nếu không có kết quả


        if (response.status == this.$enum.StatusCode.NoContent) {
          me.employees = {};
          me.totalEmp = 0;
          me.isShowSkeleton = false;
        }
      }).catch(res => {
        //xử lý lỗi...
        me.handleException(res);
      });
    },

    /**----------------------------------------
     * Hàm thực hiện load data (đã phân trang) binding vào bảng.
     * Author: quyetkaito (25/02/2022)
     ------------------------------------------*/
    loadData() {
      try {
        let me = this; //không cho phân trang

        me.disableComboboxPaging = true;
        me.dataFilter = "";
        me.employees = []; // me.employeeSelected = {};

        me.formMode = 0;
        if (!me.pageSize) me.pageSize = 10;
        if (!me.pageNumber || me.pageNumber != 1) me.pageNumber = 1; //show loading

        me.isShowSkeleton = true; //gọi API lấy dữ liệu nhân viên

        axios.get(`${LOCAL_EMPLOYEE_API_URL}/filter?pageSize=${me.pageSize}&pageNumber=${me.pageNumber}`).then(response => {
          //gán response vào danh sách nhân viên
          me.employees = response.data.Data; //gán tổng số bản ghi

          me.totalEmp = response.data.TotalRecord; //gán tổng số trang

          me.totalPage = response.data.TotalPage; //cho phép phân trang

          me.disableComboboxPaging = false;
          me.isShowSkeleton = false;
        }).catch(error => {
          //xử lý lỗi
          me.handleException(error); //ẩn loading

          me.isShowSkeleton = false;
        });
      } catch (error) {
        this.showDefaultErrorNotify();
        console.log(error);
      }
    },

    /**-------------------------------
     * Hàm xử lý mở Dialog nhân viên
     * @param {Object} data - đối tượng nhân viên truyền vào
     * @param {Number} formMode - trạng thái form thêm/sửa/xóa
     * Author: quyetkaito (16/03/2022)
     ---------------------------------*/
    openDialog({
      data,
      formMode
    }) {
      try {
        //gán formMode
        this.formMode = formMode; //nếu là thêm mới hoặc nhân bản

        if (formMode == this.$enum.FormMode.Post || formMode == this.$enum.FormMode.Clone) {
          //gọi lấy mã mới
          this.$refs.addDialog.getNewEmployeeCode();
        }

        this.employeeSelected = data;

        if (this.employeeSelected.DateOfBirth) {
          //format ngày sinh để cho vào datepicker
          this.employeeSelected.DateOfBirth = new Date(this.employeeSelected.DateOfBirth);
        }

        if (this.employeeSelected.IdentityDate) {
          //format ngày cấp cmnd để cho vào datepicker
          this.employeeSelected.IdentityDate = new Date(this.employeeSelected.IdentityDate);
        } //show dialog


        this.isShowDialog = true;
      } catch (error) {
        this.showDefaultErrorNotify();
        console.log(error);
      }
    },

    /**-------------------------------------------------
     * Thực hiện show/hide dialog truyền cho child component.
     * @param {Boolean} value - true là hiện, false là ẩn .
     * Author: quyetkaito (24/02/2022)
     ---------------------------------------------------*/
    showEmployeeDialog(value) {
      this.isShowDialog = value;
    },

    /**-------------------------------------------
     * Hàm thực hiện xóa employee.
     * @param {Object} employee - 1 đối tượng cần xóa.
     * Author: quyetkaito (22/02/2022)
     ---------------------------------------------*/
    deleteEmployee(employee) {
      const me = this; //hiện loading

      me.$refs.mLoading.show(); //2. thực hiện xóa

      axios.delete(`${LOCAL_EMPLOYEE_API_URL}/${employee.EmployeeId}`).then(function () {
        //đóng popup
        me.showMessagebox(false); //ẩn loading

        me.$refs.mLoading.hide(); //thông báo thành công

        me.toastText = Resource.SuccessDelete;
        me.$refs.msToast.show(); //load lại dữ liệu

        me.loadData();
      }).catch(error => {
        //hiển thị popup thông báo ko thành công
        me.showPopup(this.$enum.PopupType.DataError, Resource.FailDelete);
        me.handleException(error); //ẩn loading

        me.$refs.mLoading.hide();
      });
    },

    /**------------------------------------------
     * Hàm xử lý nhận sự kiện sửa từ table.
     * @param {Object} - một đối tượng cần sửa + formMode.
     * Author: quyetkaito (23/02/2022)
     --------------------------------------------*/
    handleUpdate({
      data,
      formMode
    }) {
      let me = this;

      if (data) {
        //show loading
        me.$refs.mLoading.show(); //lấy thông tin mới từ server bind vào form

        axios.get(`${LOCAL_EMPLOYEE_API_URL}/${data.employee_id}`).then(res => {
          //gán dữ liệu mới để bind vào form
          me.employeeSelected = res.data;
          me.employeeSelected.date_of_birth = new Date(me.employeeSelected.date_of_birth);
          me.employeeSelected.identity_date = new Date(me.employeeSelected.identity_date); //chuyển trạng thái submit sang sửa

          me.formMode = formMode; //show dialog

          me.showEmployeeDialog(true); //ẩn loading

          me.$refs.mLoading.hide();
        }).catch(error => {
          me.handleException(error); //ẩn loading

          me.$refs.mLoading.hide();
        });
      }
    },

    /**-----------------------------------------------
     * Hàm xử lý khi nút [Xóa] được click.
     * @param {Object} employee - 1 đối tượng cần xóa.
     * Author: quyetkaito (25/02/2022)
     ------------------------------------------------*/
    handleDelete(value) {
      if (value) {
        //gán employeSelected hiện tại truyền cho child
        this.employeeSelected = value; //hiện dialog cảnh báo

        this.showPopup(this.$enum.PopupType.DataDelete);
      }
    },

    /**----------------------------------------------
     * show thông báo lỗi mặc định cho người dùng.
     * Author: quyetkaito (8/03/2022)
     ------------------------------------------------*/
    showDefaultErrorNotify() {
      this.showPopup(this.$enum.PopupType.DataError, Resource.DefaultErrorText);
    },

    /**-------------------------------
     * Hàm xử lý exception api trả về.
     * @param {Exception} error - exception từ api trả về
     * Author: quyetkaito (08/03/2022)
     ---------------------------------*/
    handleException(error) {
      let me = this;
      let response = error.response;

      if (response.status) {
        switch (error.response.status) {
          case this.$enum.StatusCode.InternalServerError:
            {
              if (response.data.userMsg) {
                me.showPopup(this.$enum.PopupType.DataError, response.data.userMsg);
              } else {
                me.showDefaultErrorNotify();
              }

              break;
            }

          case this.$enum.StatusCode.BadRequest:
            {
              //400
              //gán message vào popup
              me.showDefaultErrorNotify();
            }
            break;

          default:
            break;
        }
      } else {
        me.showDefaultErrorNotify();
      }
    },

    /**----------------------------------
     * Hàm gọi show popup (true).
     * @param {Number} type: loại popup
     * @param {String} text: nội dung popup 
     * Author: quyetkaito (24/02/2022)
     ------------------------------------*/
    showPopup(type, text) {
      if (type) {
        this.popupType = type;
      }

      if (text) {
        this.popupText = text;
      }

      this.showMessagebox(true); //focus vào button đầu tiên(để đóng popup = phím Enter)

      this.$refs.popup.focus();
    },

    /**---------------------------------
     * Thực hiện show/hide messsagebox.
     * @param {Boolean} isShow - true là hiện/ false là ẩn.
     * Author: quyetkaito (25/02/2022)
     -----------------------------------*/
    showMessagebox(isShow) {
      this.isShowPopup = isShow;
    },

    /**---------------------------------------------------------------
     * Hàm thực hiện show table function Xóa/Nhân bản/ Ngừng sử dụng.
     * @param {Object} object - đối tượng nhân viên.
     * Author: quyetkaito (08/03/2022)
     ----------------------------------------------------------------*/
    showFunction(object) {
      if (this.isShowFunc) {
        this.isShowFunc = false;
      } else {
        this.isShowFunc = true;
        this.employeeSelected = object;
        this.formMode = this.$enum.FormMode.Clone;
      }
    },

    /**------------------------------------
     * Hàm thực hiện hide table function.
     * Author: quyetkaito (08/03/2022)
     -------------------------------*/
    hideFunction() {
      this.isShowFunc = false;
    },

    /**------------------------------
     * Get lựa chọn table function
     * @param {Number} value - giá trị của table function
     * Author: quyetkaito (08/03/2022)
     -------------------------------*/
    async getValueFromDropdown(value) {
      //nếu là xóa
      if (value == this.$enum.TableFunction.Delete) {
        this.handleDelete(this.employeeSelected);
        this.isShowFunc = false;
      } //nếu là nhân bản


      if (value == this.$enum.TableFunction.Clone) {
        //gọi hàm clone
        this.openDialog({
          data: this.employeeSelected,
          formMode: this.$enum.FormMode.Clone
        });
        await this.$refs.addDialog.getDepartment();
        this.$refs.addDialog.setDepartment();
        this.$refs.addDialog.autoFocus();
        this.isShowFunc = false;
      }
    },

    /**--------------------------------------
     * Hàm thực hiện cập nhật trang hiện tại.
     * @param {Number} page - số thứ tự của trang (từ 1)
     * Author: quyetkaito (03/03/2022)
    ----------------------------------------*/
    setCurrentPage(page) {
      this.pageNumber = page; //thực hiện filter lại

      this.filterDataServer(this.pageNumber);
    },

    /**-----------------------------------------------
     * Hàm thực hiện reset lại các tùy chọn phân trang.
     * Author: quyetkaito (03/03/2022)
    -------------------------------------------------*/
    reset() {
      this.pageNumber = 1; //mặc định trang 1

      this.pageSize = 10; //mặc định 10 bản ghi 1 trang
    }

  },
  computed: {},
  watch: {
    /**-------------------------------------------
     * Bắt sự thay đổi của pageSize. Thay đổi số bản ghi hiển thị một trang
     * Author: quyetkaito (15/03/2022)
     -------------------------------------------*/
    pageSize: function () {
      this.pageNumber = 1;

      if (this.dataFilter != "") {
        this.filterDataServer(this.pageNumber);
      } else {
        this.loadData();
      }
    },

    /**-------------------------------
     *Bắt sự thay đổi của ô tìm kiếm.
     *Author: quyetkaito (16/03/2022)
     ---------------------------------*/
    dataFilter: function () {
      this.pageNumber = 1;
    }
  }
};
